        <!-- Question Section -->
        <div class="content__article mt-5" id="sera">
            <div class="container-fuild px-4 px-md-0">
                <!-- Back Button -->
                <a style="text-decoration: none;" href="../index.html#box-main-sub">
                    <button class="cta">
                        <svg viewBox="0 0 13 10" height="10px" width="15px">
                            <path d="M1,5 L11,5"></path>
                            <polyline points="8 1 12 5 8 9"></polyline>
                        </svg>
                        <span>Back</span>

